mapper.select:
select count(*) from tbl_storage TS inner join tbl_floor TF on TF.id = TS.floorId where 11=11
and TF.categoryId = #{categoryId}
and TF.specId = #{specId}
and TF.colorCodeId = #{colorCodeId}
and TF.veinId = #{veinId}

select TS.id AS storage_id, TS.floorId AS storage_floorId, TS.tenantId AS storage_tenantId, TS.area AS storage_area, Ts.count AS storage_count, TF.number AS floor_number, TF.floorName AS floor_name, TF.sellPrice AS floor_sellPrice, TSP.specName AS spec_name, TV.veinName AS vein_name, TSI.countInStorage As countInStorage, TSI.countOrder AS countOrder, TSI.countOrderCancel AS countOrderCancel from tbl_storage TS inner join tbl_floor TF on TF.id = TS.floorId inner join tbl_spec TSP on TSP.id = TF.specId inner join tbl_vein TV on TV.id = TF.veinId inner join tbl_storage_info TSI on TSI.floorId = TS.floorId where 11=11
and TF.categoryId = #{categoryId}
and TF.specId = #{specId}
and TF.colorCodeId = #{colorCodeId}
and TF.veinId = #{veinId}
limit #{beginIndex},#{pageSize}
