mapper.select:
		select count(*) from
		tbl_floor
		where
		11=11
		
			and
			specId = #{specId}
		
		
			and
			veinId = #{veinId}
		
		
			and
			colorCodeId = #{colorCodeId}
		
		
			and
			categoryId = #{categoryId}

select f.id as f_id, f.floorName as f_floorName, f.sellPrice as f_sellPrice from tbl_floor f where
11=11
and f.floorName like "%"#{floorName}"%"
and f.specId = #{specId}
and f.categoryId = #{categoryId}
and f.veinId = #{veinId}
and f.colorCodeId = #{colorCodeId}
limit #{beginIndex},#{pageSize}
